import express from 'express';
import * as userController from '../controllers/user.controller';
import { auth } from '../middlewares/auth.middleware';


const userRouter = express.Router();

userRouter.post('/register', userController.newUser);

userRouter.post('/login', userController.login);

userRouter.put('/update', auth, userController.updateUser);

export default userRouter;

import HttpStatus from 'http-status-codes';
import * as UserService from '../services/user.service';

export const newUser = async (req, res) => {
  try {
    const data = await UserService.newUser(req.body);
    res.status(HttpStatus.CREATED).json({
      code: HttpStatus.CREATED,
      data: data,
      message: 'User created successfully'
    });
  } catch (error) {
    res.status(HttpStatus.CONFLICT).json({
      code: HttpStatus.CONFLICT,
      message: `${error}`
    });
  }
};

export const login = async (req, res) => {
  try {
    const { user, accessToken } = await UserService.login(req.body)
    res.set('Authorization', 'Bearer ' + accessToken)
    res.status(HttpStatus.OK).json({
      data: user,
      message: "Login Successfull"
    })
  } catch (error) {
    res.status(HttpStatus.CONFLICT).json({
      code: HttpStatus.CONFLICT,
      message: `${error}`
    });
  }
}

export const updateUser = async (req, res) => {
  try {
    const userId = req.params._id;
    const data = await UserService.updateUser(req.body, userId);
    res.status(HttpStatus.OK).json({
      code: HttpStatus.OK,
      data: data,
      message: 'User profile updated successfully'
    });
  } catch (error) {
    res.status(HttpStatus.CONFLICT).json({
      code: HttpStatus.CONFLICT,
      message: `${error}`
    });
  }
};

import bcrypt from "bcrypt";
import User from "../models/user.class";
import jwt from "jsonwebtoken";
import { ObjectId } from "mongodb";

export const collection = () => {
  return require('../config/database').userCollection;
}

export const newUser = async (body) => {
  const { fullName, userName, email, password, bio } = body
  const data = await collection().findOne({ email: email });
  if (data != null) {
    throw new Error('User Already Exist')
  }
  else {
    const salt = await bcrypt.genSalt(10);
    const hashPassword = await bcrypt.hash(password, salt);
    const userData = new User(fullName, userName, email, hashPassword, bio)
    return collection().insertOne(userData);
  }
}

export const login = async (body) => {
  const { email, password } = body
  const data = await collection().findOne({ email: email });
  if (data == null) {
    throw new Error('User does not exist with this email')
  }
  else {
    const validPassword = await bcrypt.compare(password, data.password);
    if (validPassword) {

      let accessToken = jwt.sign({ email: data.email, id: data._id, userName: data.userName }, process.env.ACCESS_SECRET_KEY);

      return { data, accessToken };
    } else {
      throw new Error("Not a Valid Password");
    }
  }
}

export const updateUser = async (body, userID) => {
  const { userName, bio } = body
  const data = collection().findOneAndUpdate({ _id: ObjectId(`${userID}`) },
    {
      $set: {
        userName: userName,
        bio: bio,
      },
    },
    {
      upsert: true,
      returnNewDocument: true,
    }
  )
  return data;
}

class User {    
      constructor(fullName, userName, email, password, bio){
        this.fullName = fullName
        this.userName = userName;
        this.email = email;
        this.password = password;
        this.bio = bio
    }
           
}
export default User

import HttpStatus from 'http-status-codes';
import jwt from 'jsonwebtoken';

export const auth = async (req, _res, next) => {
  try {
    let bearerToken = req.header('Authorization');
    if (!bearerToken) {
      throw new Error(`${HttpStatus.BAD_REQUEST} : Authorization token is required`);
    }
    bearerToken = bearerToken.split(' ')[1];

    jwt.verify(bearerToken, process.env.ACCESS_SECRET_KEY, (err, decodedData) => {
      if (err) {
        throw new Error(`${HttpStatus.UNAUTHORIZED} : User not authenticated`);
      } else {
        req.body['data'] = decodedData;

        next();
      }
    });
  }
  catch (error) {
    next(error);
  }
};

export const userAuth = async (req, _res, next) => {
  try {
    let bearerToken = req.header('Authorization');
    if (!bearerToken) {
      throw new Error(`${HttpStatus.BAD_REQUEST} : Authorization token is required`);

    }
    bearerToken = bearerToken.split(' ')[1];

    jwt.verify(bearerToken, process.env.ACCESS_SECRET_KEY, (err, decodedData) => {
      if (err) {
        throw new Error(`${HttpStatus.UNAUTHORIZED} : User not authenticated`);
      } else {
        req.body['data'] = decodedData;
        req.body.userId = decodedData.id;
        req.body.userName = decodedData.userName;

        next();
      }
    });
  }
  catch (error) {
    next(error);
  }
};

